styles.css:
a {
  text-decoration: none;
}
body {
  overflow-x: hidden;
  scroll-behavior: smooth;
}
:where(.wp-site-blocks) > * {
  margin-block-start: 0px !important;
}
::-webkit-scrollbar {
  width: 10px;
}

::-webkit-scrollbar-track {
  background: var(--wp--preset--color--custom-color-1);
}

::-webkit-scrollbar-thumb {
  background: var(--wp--preset--color--custom-color-2);
  border-radius: 10px;
}
.marquee-text {
  white-space: nowrap;
}
.shadow-marquee {
  position: relative;
  z-index: 999;
  bottom: 70px;
  width: 100%;
  margin-bottom: -70px !important;
}
.wp-block-navigation-link,
.wp-block-navigation-submenu {
  padding: 16px 20px !important;
  border-radius: 6px !important;
  background-color: var(--wp--preset--color--custom-color-9) !important;
}
details summary {
  list-style: none;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
}
details summary::after {
  content: "\2A2F";
  transform: rotate(45deg);
  font-size: 30px;
  transition: 0.5s all;
  width: 40px;
  height: 40px;
  line-height: 40px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 50%;
  font-weight: 400;
  background: var(--wp--preset--color--custom-color-8);
  color: var(--wp--preset--color--custom-color-7);
}
details[open] summary::after {
  content: "\2A2F";
  transform: rotate(0deg);
}
.outer {
  overflow-x: hidden;
}
.slider {
  width: 300%;
  height: 100vh;
  display: flex;
  flex-wrap: nowrap;
}
.section_x {
  height: 100vh;
  width: 100%;
}
.product {
  display: flex;
  flex-direction: column;
  align-items: stretch;
  gap: 0;
  border-radius: 10px;
  padding: 0;
  background-color: var(--wp--preset--color--custom-color-9) !important;
}
.product img {
  aspect-ratio: 4 / 3;
  object-fit: cover;
  box-sizing: border-box;
  height: auto;
  max-width: 100%;
  vertical-align: bottom;
}
.price-cart {
  background-color: var(--wp--preset--color--custom-color-8) !important;
  border-radius: 0px 0px 10px 10px;
}
.product > div {
  margin-bottom: 0px !important;
  height: 100%;
}
.wp-block-button.wc-block-components-product-button {
  align-items: flex-start;
}
.wp-block-button.wc-block-components-product-button
  .wc-block-components-product-button__button {
  align-items: center;
  border-style: none;
  display: inline-flex;
  justify-content: center;
  line-height: inherit;
  overflow: hidden;
  white-space: normal;
  word-break: break-word;
  border-radius: 6px;
  background-color: var(--wp--preset--color--custom-color-3);
  color: var(--wp--preset--color--black);
  text-transform: uppercase;
  font-family: "Roboto Mono";
  margin-bottom: 0 !important;
}
.wp-block-post-title {
  display: inline-block;
  font-weight: normal;
  text-transform: uppercase;
  text-align: start;
  margin-bottom: 0;
}
.woocommerce-Price-currencySymbol {
  font-size: var(--wp--preset--font-size--small);
  color: var(--wp--preset--color--custom-color-3);
}
.woocommerce-Price-amount.amount {
  font-size: var(--wp--preset--font-size--medium);
  text-align: start !important;
  font-family: "Alegreya Sans SC";
  font-weight: bold;
}
.wc-block-grid__product-price {
  text-align: start !important;
}
.wp-block-woocommerce-product-price {
  margin-bottom: 0;
}
.arrow {
  transition: 0.7s all;
  cursor: pointer;
}
.arrow > p {
  color: var(--wp--preset--color--custom-color-3) !important;
}
.arrow:hover {
  transform: rotate(-45deg);
}
.woocommerce.wc-block-catalog-sorting select.orderby {
  font-size: inherit;
  padding: 10px 20px;
  border-radius: 6px;
  background-color: var(--wp--preset--color--custom-color-1);
  color: var(--wp--preset--color--custom-color-5) !important;
  border-color: var(--wp--preset--color--custom-color-5);
}
.wp-block-search__input {
  appearance: none;
  border: 1px solid var(--wp--preset--color--custom-color-5) !important;
  flex-grow: 1;
  margin-left: 0;
  margin-right: 0;
  min-width: 3rem;
  border-radius: 6px;
  background-color: transparent;
  padding: 8px;
  color: var(--wp--preset--color--custom-color-5) !important;
  text-decoration: unset !important;
}
.wp-block-search__button {
  background-color: var(--wp--preset--color--custom-color-3);
  border-width: 0;
  color: var(--wp--preset--color--custom-color-1) !important;
  font-family: var(--wp--preset--font-family--roboto-mono) !important;
  color: #fff;
  text-transform: uppercase;
  font-family: inherit;
  font-size: inherit;
  line-height: inherit;
  padding: calc(0.667em + 2px) calc(1.333em + 2px);
  text-decoration: none;
  border-radius: 6px;
}
@media screen and (max-width: 768px) {
  body {
    --wp--preset--font-size--small: 15px !important;
    .marquee-text {
      white-space: normal !important;
      text-align: center;
      padding: 20px;
      display: none;
    }
    --wp--preset--font-size--medium: 24px !important;
    --wp--preset--font-size--large: 35px !important;
    --wp--preset--font-size--x-large: 45px !important;
    --wp--preset--spacing--20: 0.2rem !important;
    --wp--preset--spacing--30: 0.5rem !important;
    --wp--preset--spacing--40: 0.8rem !important;
    --wp--preset--spacing--50: 1.2rem !important;
    --wp--preset--spacing--60: 2rem !important;
    --wp--preset--spacing--70: 3rem !important;
    --wp--preset--spacing--80: 4rem !important;
  }
  .break-row {
    flex-direction: column !important;
  }
  .break-reverse {
    flex-direction: column-reverse !important;
  }
  .w100 {
    width: 100% !important;
    flex-basis: 100% !important;
  }
  .slider {
    height: auto !important;
  }
  .section_x {
    height: auto !important;
  }
}
.size_header {
  width: 40vw;
}
a:where(:not(.wp-element-button)) {
  text-decoration: underline;
}
a:where(:not(.wp-block-navigation-link)) {
  text-decoration: underline;
}
a:where(:not(.wp-block-navigation-item__content)) {
  text-decoration: underline !important;
}

.marquee {
  position: relative;
  overflow: hidden;
  --offset: 20vw;
  --move-initial: calc(-25% + var(--offset));
  --move-final: calc(-50% + var(--offset));
}

.marquee__inner {
  width: fit-content;
  display: flex;
  position: relative;
  transform: translate3d(var(--move-initial), 0, 0);
  animation: marquee 7s linear infinite;
  animation-play-state: paused;
  animation-play-state: running;
}

.marquee__inner h4 {
  white-space: nowrap;
  overflow: hidden;
}

@keyframes marquee {
  0% {
    transform: translate3d(var(--move-initial), 0, 0);
  }

  100% {
    transform: translate3d(var(--move-final), 0, 0);
  }
}

.block-editor-block-list__block {
  animation: none !important;
}
